 */
function setAttribute(dom, name, value) {
  // 如果属性名是classname,则改回class
  if (name === 'className') name = 'class';

  // 如果属性名是onXXX,则是一个事件监听方法
  if (/on\w+/.test(name)) {
    name = name.toLowerCase();
    dom[name] = value || ''
    // 如果属性名是style,则更新style对象
  } else if (name === 'style') {
    if (!value || typeof value === 'string') {
      dom.style.cssText = value || ''
    } else if (value && typeof value === 'object') {
      for(let name in value) {
        dom.style[name] = typeof value[name] === 'number' ? value[name] + 'px' : value[name];
      }
    }
  } else {
    if (name !== "class" && name in dom) {
      dom[name] = value || ''
    }

    if (value) {
      dom.setAttribute(name, value)
    } else {
      dom.removeAttribute(name)
    }
  }
  // 普通属性则直接更新属性
}
/**
 * createComponent方法用来创建组件实例，并且将函数定义组件扩展为类定义组件进行处理，以免其他地方需要区分不同定义方式。
 * @param {*} component 
 * @param {*} props 
 */
function createComponent(component, props) {
  let inst 
     // 如果是类定义组件，则直接返回实例
     if ( component.prototype && component.prototype.render ) {
      inst = new component( props );
  // 如果是函数定义组件，则将其扩展为类定义组件
  } else {
      inst = new Component( props );
      inst.constructor = component;
      inst.render = function() {
          return this.constructor( props );
      }
  }

  return inst;
}

/**
 * setComponentProps方法用来更新props，在其中可以实现componentWillMount，componentWillReceiveProps两个生命周期方法
 * @param {*} component 
 * @param {*} props 
 */
function setComponentProps( component, props ) {

  if ( !component.base ) {
      if ( component.componentWillMount ) component.componentWillMount();
  } else if ( component.componentWillReceiveProps ) {
      component.componentWillReceiveProps( props );
  }

  component.props = props;

  renderComponent( component );
}
/**
 * renderComponent方法用来渲染组件，setState方法中会直接调用这个方法进行重新渲染，
 * 在这个方法里可以实现componentWillUpdate，
 * componentDidUpdate，componentDidMount几个生命周期方法。
 * @param {*} component 
 */
export function renderComponent( component ) {

  let base;

  const renderer = component.render();

  if ( component.base && component.componentWillUpdate ) {
      component.componentWillUpdate();
  }

  base = _render( renderer );

  if ( component.base ) {
      if ( component.componentDidUpdate ) component.componentDidUpdate();
  } else if ( component.componentDidMount ) {
      component.componentDidMount();
  }

  if ( component.base && component.base.parentNode ) {
      component.base.parentNode.replaceChild( base, component.base );
  }

  component.base = base;
  base._component = component;

}
/**
  所以render的第一个参数实际上接受的是createElement返回的对象，也就是虚拟DOM
  而第二个参数则是挂载的目标DOM

  总而言之，render方法的作用就是将虚拟DOM渲染成真实的DOM，下面是它的实现： 
*/
function _render ( vnode, container ) {
  if (vnode === undefined || vnode === null || typeof vnode === 'boolean') vnode = '';

  if (typeof vnode === 'number') vnode = string(vnode);

  if (typeof vnode === 'string') {
    const textNode = document.createTextNode(vnode);
    return textNode
  }

  // 渲染组件
  if (typeof vnode === 'function') {
    const component = createComponent(vnode.tag, vode.attrs)
    setComponentProps(component, vnode.attrs)
    return component.base;
  }

  const dom = document.createElement(vnode.tag);

  if(vnode.attrs) {
    Object.keys(vnode.attrs).forEach( key => {
      const value = vnode.attrs[key]
      setAttribute(dom, key, value); // 设置属性
    })
  }

  vnode.children.forEach(child => render(child, dom))

  return dom
}

function render ( vnode, container ) {
  return container.appendChild(_render(vnode))
}

export const ReactDOM = {
  render: (vnode, container) => {
    container.innerHTML = ''
    return render(vnode, container)
  }
}
